int
TriggerEvtConfigTable_LoadFile(char *File, TriggerEventCfg_T *Trigger)
{
        int len = 0;
        UN_USED(len);
	INI_HANDLE ini;
	ini=IniLoadFile(File);
	if (ini == NULL)
		return -1;

	Trigger->CriticalFlag = IniGetUInt(ini, "TriggerEventConfiguration", "CriticalFlag", 0);

	Trigger->NONCriticalFlag = IniGetUInt(ini, "TriggerEventConfiguration", "NONCriticalFlag", 0);

	Trigger->NONRecoverableFlag = IniGetUInt(ini, "TriggerEventConfiguration", "NONRecoverableFlag", 0);

	Trigger->FanTroubled = IniGetUInt(ini, "TriggerEventConfiguration", "FanTroubled", 0);

	Trigger->WDTTimeExpire = IniGetUInt(ini, "TriggerEventConfiguration", "WDTTimeExpire", 0);

	Trigger->SysDConFlag = IniGetUInt(ini, "TriggerEventConfiguration", "SysDConFlag", 0);

	Trigger->SysDCoffFlag = IniGetUInt(ini, "TriggerEventConfiguration", "SysDCoffFlag", 0);

	Trigger->SysResetFlag = IniGetUInt(ini, "TriggerEventConfiguration", "SysResetFlag", 0);

	Trigger->LPCResetFlag = IniGetUInt(ini, "TriggerEventConfiguration", "LPCResetFlag", 0);

	Trigger->SpecDateTime = IniGetUInt(ini, "TriggerEventConfiguration", "SpecDateTime", 0);

	Trigger->Time = IniGetUInt(ini, "TriggerEventConfiguration", "Time", 0x0);

	IniCloseFile(ini);
	return 0;
}

int
TriggerEvtConfigTable_SaveFile(char *File, TriggerEventCfg_T *Trigger)
{
	static INI_HANDLE ini = NULL;
	if(ini == NULL)
	{
		ini=IniLoadFile(File);
		if (ini == NULL)
			return -1;
	}

	IniSetUInt(ini, "TriggerEventConfiguration", "CriticalFlag", Trigger->CriticalFlag);

	IniSetUInt(ini, "TriggerEventConfiguration", "NONCriticalFlag", Trigger->NONCriticalFlag);

	IniSetUInt(ini, "TriggerEventConfiguration", "NONRecoverableFlag", Trigger->NONRecoverableFlag);

	IniSetUInt(ini, "TriggerEventConfiguration", "FanTroubled", Trigger->FanTroubled);

	IniSetUInt(ini, "TriggerEventConfiguration", "WDTTimeExpire", Trigger->WDTTimeExpire);

	IniSetUInt(ini, "TriggerEventConfiguration", "SysDConFlag", Trigger->SysDConFlag);

	IniSetUInt(ini, "TriggerEventConfiguration", "SysDCoffFlag", Trigger->SysDCoffFlag);

	IniSetUInt(ini, "TriggerEventConfiguration", "SysResetFlag", Trigger->SysResetFlag);

	IniSetUInt(ini, "TriggerEventConfiguration", "LPCResetFlag", Trigger->LPCResetFlag);

	IniSetUInt(ini, "TriggerEventConfiguration", "SpecDateTime", Trigger->SpecDateTime);

	IniSetUInt(ini, "TriggerEventConfiguration", "Time", Trigger->Time);

	IniSaveFile(ini,File);
	if(g_corefeatures.dual_image_support != ENABLED)
	{
		IniCloseFile(ini);
		ini = NULL;
	}
	return 0;
}

