#include <stdio.h>
#include <string.h>
#include "parse-ex.h"
#include "IPMIConf.h"

        #include "Types.h"
        #include "IPMIConf.h"
        #include "LANConfig.h"
        
int
BondConfig_LoadFile(char *File, BondIface *data)
{
        int len = 0;
        UN_USED(len);
	INI_HANDLE ini;
	ini=IniLoadFile(File);
	if (ini == NULL)
		return -1;

	data->Enable = IniGetUInt(ini, "BondConfig", "Enable", 0x0);

	data->BondIndex = IniGetUInt(ini, "BondConfig", "BondIndex", 0);

	data->BondMode = IniGetUInt(ini, "BondConfig", "BondMode", 0x1);

	data->MiiInterval = IniGetUInt(ini, "BondConfig", "MiiInterval", 100);

	data->Slaves = IniGetUInt(ini, "BondConfig", "Slaves", 3);

	data->AutoConf = IniGetUInt(ini, "BondConfig", "AutoConf", 1);

	IniCloseFile(ini);
	return 0;
}

int
BondConfig_SaveFile(char *File, BondIface *data)
{
	static INI_HANDLE ini = NULL;
	if(ini == NULL)
	{
		ini=IniLoadFile(File);
		if (ini == NULL)
			return -1;
	}

	IniSetUInt(ini, "BondConfig", "Enable", data->Enable);

	IniSetUInt(ini, "BondConfig", "BondIndex", data->BondIndex);

	IniSetUInt(ini, "BondConfig", "BondMode", data->BondMode);

	IniSetUInt(ini, "BondConfig", "MiiInterval", data->MiiInterval);

	IniSetUInt(ini, "BondConfig", "Slaves", data->Slaves);

	IniSetUInt(ini, "BondConfig", "AutoConf", data->AutoConf);

	IniSaveFile(ini,File);
	if(g_corefeatures.dual_image_support != ENABLED)
	{
		IniCloseFile(ini);
		ini = NULL;
	}
	return 0;
}

